	/**
	 * Selects a collection of {@link Comments} objects with a {@link CommentsI18n} translation populated.
	 *
	 * @param Criteria  $criteria
	 * @param string    $culture
	 * @param PropelPDO $con
	 * @param string    $join_behavior
	 *
	 * @return array
	 */
	static public function doSelectWithI18n(Criteria $criteria, $culture = null, $con = null, $join_behavior = Criteria::LEFT_JOIN)
	{
	  $criteria = clone $criteria;
	
	  if (null === $culture)
	  {
	    $culture = sfPropel::getDefaultCulture();
	  }
	
	  // Set the correct dbName if it has not been overridden
	  if ($criteria->getDbName() == Propel::getDefaultDB()) {
	  	$criteria->setDbName(self::DATABASE_NAME);
	  }
	
	  CommentsPeer::addSelectColumns($criteria);
	  $startcol = (CommentsPeer::NUM_COLUMNS - CommentsPeer::NUM_LAZY_LOAD_COLUMNS);
	  CommentsI18nPeer::addSelectColumns($criteria);
	  // http://bsds.etapasvi.com/issues/113 
	  //$criteria->addJoin(CommentsPeer::ID, CommentsI18nPeer::ID, $join_behavior);
	  //$criteria->add(CommentsI18nPeer::CULTURE, $culture);
	  $criteria->addJoin(array(CommentsPeer::ID, CommentsI18nPeer::CULTURE), array(CommentsI18nPeer::ID, "'$culture'"), $join_behavior);
	
	  foreach (sfMixer::getCallables('BaseComments:doSelectJoin:doSelectJoin') as $sf_hook)
	  {
	    call_user_func($sf_hook, 'Comments', $criteria, $con);
	  }
	
	  $stmt = BasePeer::doSelect($criteria, $con);
		$results = array();
	
		while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
			$key1 = CommentsPeer::getPrimaryKeyHashFromRow($row, 0);
			if (null !== ($obj1 = CommentsPeer::getInstanceFromPool($key1))) {
				// We no longer rehydrate the object, since this can cause data loss.
	  		// See http://propel.phpdb.org/trac/ticket/509
	  		// $obj1->hydrate($row, 0, true); // rehydrate
	  	} else {
				$cls = CommentsPeer::getOMClass(false);
				$obj1 = new $cls();
				$obj1->hydrate($row);
	      CommentsPeer::addInstanceToPool($obj1, $key1);
			} // if $obj1 already loaded
	
			$key2 = CommentsI18nPeer::getPrimaryKeyHashFromRow($row, $startcol);
			if ($key2 !== null) {
				$obj2 = CommentsI18nPeer::getInstanceFromPool($key2);
				if (!$obj2) {
					$cls = CommentsI18nPeer::getOMClass(false);
					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol);
					CommentsI18nPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 already loaded
	
	      $obj1->setCommentsI18nForCulture($obj2, $culture);
			} // if joined row was not null
	
			$results[] = $obj1;
		}
	
		$stmt->closeCursor();
	
		return $results;
	}

