					$this->setNew(false);
				} else {
					$affectedRows += PhotoPeer::doUpdate($this, $con);
				}

				$this->resetModified(); // [HL] After being saved an object is no longer 'modified'
			}

			if ($this->collPhotoI18ns !== null) {
				foreach ($this->collPhotoI18ns as $referrerFK) {
