	/**
	 * Returns the current translation.
	 *
	 * @return PhotoI18n
	 */
	public function getCurrentPhotoI18n($culture = null)
	{
	  if (null === $culture)
	  {
	    $culture = null === $this->culture ? sfPropel::getDefaultCulture() : $this->culture;
	  }
	
	  if (!isset($this->current_i18n[$culture]))
	  {
	    $object = $this->isNew() ? null : PhotoI18nPeer::retrieveByPK($this->getPrimaryKey(), $culture);
	    if ($object)
	    {
	      $this->setPhotoI18nForCulture($object, $culture);
	    }
	    else
	    {
	      $this->setPhotoI18nForCulture(new PhotoI18n(), $culture);
	      $this->current_i18n[$culture]->setCulture($culture);
	    }
	  }
	
	  return $this->current_i18n[$culture];
	}
	
	/**
	 * Sets the translation object for a culture.
	 */
	public function setPhotoI18nForCulture(PhotoI18n $object, $culture)
	{
	  $this->current_i18n[$culture] = $object;
	  $this->addPhotoI18n($object);
	}

